import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import './OrdersPage.css'; 

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [orderDetails, setOrderDetails] = useState({
    products: [],
    delivery: {},
    warehouse: {}
  });
  const [formData, setFormData] = useState({
    products: [],
    deliveryService: '',
    warehouseId: ''
  });

  // –ó–∞–≥—Ä—É–∑–∫–∞ —Å–ø–∏—Å–∫–∞ –∑–∞–∫–∞–∑–æ–≤
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/orders');
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–µ—Ç–∞–ª–µ–π –∑–∞–∫–∞–∑–∞
  const fetchOrderDetails = async (orderId) => {
    try {
      const [orderRes, productsRes, deliveryRes] = await Promise.all([
        fetch(`http://localhost:5000/api/orders/${orderId}`),
        fetch(`http://localhost:5000/api/orders/${orderId}/products`),
        fetch(`http://localhost:5000/api/orders/${orderId}/delivery`)
      ]);

      if (!orderRes.ok || !productsRes.ok || !deliveryRes.ok) {
        throw new Error('Failed to fetch order details');
      }

      const [order, products, delivery] = await Promise.all([
        orderRes.json(),
        productsRes.json(),
        deliveryRes.json()
      ]);

      // // –ü–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Å–∫–ª–∞–¥–µ –∏–∑ –ø–µ—Ä–≤–æ–≥–æ —Ç–æ–≤–∞—Ä–∞
      // const warehouseResponse = await fetch(`http://localhost:5000/api/warehouses/${products[0]?.warehouseId}`);
      // const warehouse = warehouseResponse.ok ? await warehouseResponse.json() : {};

      setOrderDetails({
        ...order,
        products,
        delivery,
        // warehouse
      });

      setFormData({
        products: products.map(p => ({
          productId: p.productId,
          quantity: p.quantity,
          price: p.price
        })),
        deliveryService: delivery.service,
        // warehouseId: products[0]?.warehouseId || ''
      });
    } catch (err) {
      setError(err.message);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –º–æ–¥–∞–ª—å–Ω—ã—Ö –æ–∫–æ–Ω
  const handleViewClick = async (order) => {
    setSelectedOrder(order);
    await fetchOrderDetails(order.id);
    setIsViewModalOpen(true);
  };

  const handleEditClick = async (order) => {
    setSelectedOrder(order);
    await fetchOrderDetails(order.id);
    setIsEditModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsViewModalOpen(false);
    setIsEditModalOpen(false);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏–π —Ñ–æ—Ä–º—ã
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleProductChange = (index, field, value) => {
    const updatedProducts = [...formData.products];
    updatedProducts[index][field] = value;
    setFormData(prev => ({ ...prev, products: updatedProducts }));
  };

  // –û—Ç–ø—Ä–∞–≤–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏–π
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/orders/${selectedOrder.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) throw new Error('Failed to update order');

      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –∑–∞–∫–∞–∑–æ–≤
      const ordersResponse = await fetch('http://localhost:5000/api/orders');
      const ordersData = await ordersResponse.json();
      setOrders(ordersData);

      setIsEditModalOpen(false);
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...</div>;
  if (error) return <div className="error">–û—à–∏–±–∫–∞: {error}</div>;

  return (
    <div className="home-page">
      <Header />

      <section className="hero">
        <h1>–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∑–∞–∫–∞–∑–∞–º–∏</h1>
        <p>–ü—Ä–æ—Å–º–æ—Ç—Ä, —Å–æ–∑–¥–∞–Ω–∏–µ –∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–∫–∞–∑–æ–≤ –ø–æ—Ç—Ä–µ–±–∏—Ç–µ–ª–µ–π</p>
      </section>

      <section className="dashboard">
        <div className="dashboard-grid">
          <Link to="/orders/new" className="dashboard-card">
            <h3>‚ûï –°–æ–∑–¥–∞—Ç—å –Ω–æ–≤—ã–π –∑–∞–∫–∞–∑</h3>
            <p>–†—É—á–Ω–æ–µ —Å–æ–∑–¥–∞–Ω–∏–µ –∑–∞–∫–∞–∑–∞</p>
          </Link>
          <Link to="/orders/today" className="dashboard-card">
            <h3>üìÖ –ó–∞–∫–∞–∑—ã –Ω–∞ —Å–µ–≥–æ–¥–Ω—è</h3>
            <p>–ü—Ä–æ—Å–º–æ—Ç—Ä —Å–µ–≥–æ–¥–Ω—è—à–Ω–∏—Ö –∑–∞–∫–∞–∑–æ–≤</p>
          </Link>
          <Link to="/orders/search" className="dashboard-card">
            <h3>üîç –ü–æ–∏—Å–∫ –∑–∞–∫–∞–∑–æ–≤</h3>
            <p>–ü–æ–∏—Å–∫ –ø–æ –Ω–æ–º–µ—Ä—É, –∫–ª–∏–µ–Ω—Ç—É –∏–ª–∏ –¥–∞—Ç–µ</p>
          </Link>
        </div>
      </section>

      <section className="description">
        <h2>–°–ø–∏—Å–æ–∫ –∑–∞–∫–∞–∑–æ–≤</h2>
        <div className="orders-table">
          <table>
            <thead>
              <tr>
                <th>‚Ññ –ó–∞–∫–∞–∑–∞</th>
                <th>–ü–æ—Ç—Ä–µ–±–∏—Ç–µ–ª—å</th>
                <th>–î–∞—Ç–∞</th>
                <th>–°—É–º–º–∞</th>
                <th>–°—Ç–∞—Ç—É—Å</th>
                <th>–î–µ–π—Å—Ç–≤–∏—è</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.customer}</td>
                  <td>{new Date(order.date).toLocaleDateString('ru-RU')}</td>
                  <td>{order.amount.toLocaleString()} ‚ÇΩ</td>
                  <td>
                    <span className={`status-badge status-${order.status.toLowerCase().replace(' ', '-')}`}>
                      {order.status}
                    </span>
                  </td>
                  <td>
                    <button 
                      onClick={() => handleViewClick(order)} 
                      className="action-link"
                    >
                      –ü—Ä–æ—Å–º–æ—Ç—Ä
                    </button>
                    <button 
                      onClick={() => handleEditClick(order)} 
                      className="action-link"
                    >
                      –ò–∑–º–µ–Ω–∏—Ç—å
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ */}
      {isViewModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>–î–µ—Ç–∞–ª–∏ –∑–∞–∫–∞–∑–∞ #{selectedOrder.id}</h2>
            <div className="modal-content">
              <div className="order-section">
                <h3>–û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h3>
                <p><strong>–ü–æ—Ç—Ä–µ–±–∏—Ç–µ–ª—å:</strong> {selectedOrder.customer}</p>
                <p><strong>–î–∞—Ç–∞:</strong> {new Date(selectedOrder.date).toLocaleDateString('ru-RU')}</p>
                <p><strong>–°—É–º–º–∞:</strong> {selectedOrder.amount.toLocaleString()} ‚ÇΩ</p>
                <p><strong>–°—Ç–∞—Ç—É—Å:</strong> {selectedOrder.status}</p>
              </div>

              <div className="order-section">
                <h3>–¢–æ–≤–∞—Ä—ã</h3>
                <table className="products-table">
                  <thead>
                    <tr>
                      <th>–¢–æ–≤–∞—Ä</th>
                      <th>–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ</th>
                      <th>–¶–µ–Ω–∞</th>
                      <th>–°—É–º–º–∞</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderDetails.products.map((product, index) => (
                      <tr key={index}>
                        <td>{product.name}</td>
                        <td>{product.quantity} {product.unit}</td>
                        <td>{product.price} ‚ÇΩ</td>
                        <td>{product.price * product.quantity} ‚ÇΩ</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              <div className="order-section">
                <h3>–î–æ—Å—Ç–∞–≤–∫–∞</h3>
                <p><strong>–°–ª—É–∂–±–∞ –¥–æ—Å—Ç–∞–≤–∫–∏:</strong> {orderDetails.delivery.service}</p>
                <p><strong>–î–∞—Ç–∞ –æ—Ç–≥—Ä—É–∑–∫–∏:</strong> {new Date(orderDetails.delivery.dateFrom).toLocaleDateString('ru-RU')}</p>
                <p><strong>–î–∞—Ç–∞ –ø—Ä–∏–±—ã—Ç–∏—è:</strong> {new Date(orderDetails.delivery.dateTo).toLocaleDateString('ru-RU')}</p>
              </div>

              {/* <div className="order-section">
                <h3>–°–∫–ª–∞–¥</h3>
                <p><strong>–ê–¥—Ä–µ—Å:</strong> {orderDetails.warehouse.address}</p>
                <p><strong>–¢–µ–∫—É—â–∞—è –∑–∞–ø–æ–ª–Ω–µ–Ω–Ω–æ—Å—Ç—å:</strong> {orderDetails.warehouse.curFullness} –º¬≥</p>
              </div> */}
            </div>
            <button onClick={handleCloseModal} className="modal-close">–ó–∞–∫—Ä—ã—Ç—å</button>
          </div>
        </div>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      {isEditModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–∫–∞–∑–∞ #{selectedOrder.id}</h2>
            <form onSubmit={handleSubmit} className="modal-content">
              <div className="form-section">
                <h3>–¢–æ–≤–∞—Ä—ã</h3>
                <table className="products-table">
                  <thead>
                    <tr>
                      <th>–¢–æ–≤–∞—Ä</th>
                      <th>–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ</th>
                      <th>–¶–µ–Ω–∞</th>
                    </tr>
                  </thead>
                  <tbody>
                    {formData.products.map((product, index) => (
                      <tr key={index}>
                        <td>
                          <select
                            value={product.productId}
                            onChange={(e) => handleProductChange(index, 'productId', e.target.value)}
                          >
                            {orderDetails.products.map(p => (
                              <option key={p.productId} value={p.productId}>{p.name}</option>
                            ))}
                          </select>
                        </td>
                        <td>
                          <input
                            type="number"
                            value={product.quantity}
                            onChange={(e) => handleProductChange(index, 'quantity', parseInt(e.target.value))}
                            min="1"
                          />
                        </td>
                        <td>
                          <input
                            type="number"
                            value={product.price}
