import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import './OrdersPage.css'; 

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/orders');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <div className="loading">Загрузка данных...</div>;
  if (error) return <div className="error">Ошибка: {error}</div>;


  return (
    <div className="home-page">
      <Header/>

      {/* Основной контент страницы заказов */}
      <section className="hero">
        <h1>Управление заказами</h1>
        <p>Просмотр, создание и редактирование заказов потребителей</p>
      </section>

      {/* Кнопки действий */}
      <section className="dashboard">
        <div className="dashboard-grid">
          <Link to="/orders/new" className="dashboard-card">
            <h3>➕ Создать новый заказ</h3>
            <p>Ручное создание заказа</p>
          </Link>
          <Link to="/orders/today" className="dashboard-card">
            <h3>📅 Заказы на сегодня</h3>
            <p>Просмотр сегодняшних заказов</p>
          </Link>
          <Link to="/orders/search" className="dashboard-card">
            <h3>🔍 Поиск заказов</h3>
            <p>Поиск по номеру, клиенту или дате</p>
          </Link>
        </div>
      </section>

      {/* Таблица заказов */}
      <section className="description">
        <h2>Список заказов</h2>
        <div className="orders-table">
          <table>
            <thead>
              <tr>
                <th>№ Заказа</th>
                <th>Потребитель</th>
                <th>Дата</th>
                <th>Сумма</th>
                <th>Статус</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.customer}</td>
                  <td>{order.date}</td>
                  <td>{order.amount.toLocaleString()} ₽</td>
                  <td>
                    <span className={`status-badge status-${order.status.toLowerCase().replace(' ', '-')}`}>
                      {order.status}
                    </span>
                  </td>
                  <td>
                    <Link to={`/orders/${order.id}`} className="action-link">Просмотр</Link>
                    <Link to={`/orders/${order.id}/edit`} className="action-link">Изменить</Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

    <Footer/>
    </div>
  );
};

export default OrdersPage;
